import React, { Component } from 'react';
import todoData from './TodoData';
import { ToDoItem } from "./ToDoItem";


export class Body extends Component {
    constructor() {
        super();
        this.state = {
            todoData: todoData,
        };
        this.handleChange = this.handleChange.bind(this);
    }

    handleChange(idToMatch) {
        this.setState(prevState => {
            // let newArray;
            // newArray = foreach(prevState.todoData.foreach(todo => {
            //     if(todo.id == idToMatch){
            //         todo.completed = !todo.completed
            //     }
            //     return
            // }))

            const updatedTodos = prevState.todoData.map(t => t);
            updatedTodos.array.forEach(element => {
                if (element.id === idToMatch) {
                    element.completed = !element.completed;
                }
            });


            return {
                todoData: prevState.todoData.map(t => 
                    t
                    // t.id == id ? !t.completed : t.completed;
                    // console.log(t);

                    // t.id == id ? !t.completed : t.completed
                )
            }
        })
        console.log("Changed", idToMatch);
    }

    render() {
        const todoComponents = this.state.todoData.map(
            todo => <ToDoItem id={todo.id} text={todo.text} completed={todo.completed} onChange={this.handleChange} />);

        return (<div className="content">
            <h2 className="heading">These are my to do</h2>
            {todoComponents}
        </div>);
    }
}
